let download=element=>{let file_name=element.getAttribute("download-id");window.open("/download/"+file_name)},openFolder=element=>{let folder_name=element.getAttribute("folder-id"),file_list=document.querySelector(".file-list");fetch("/folder/?folder_name="+folder_name).then(response=>response.text()).then(data=>{file_list.innerHTML=data})},resize_file_names=()=>{let w=window.innerWidth,file_name=document.getElementsByClassName("file-name"),file_name_new_length=Math.floor(w/100+5);w<1e3&&Array.from(file_name).forEach(element=>{element.textContent.length>file_name_new_length&&element.textContent.length!=file_name_new_length&&(element.textContent=element.textContent.substr(0,file_name_new_length)+"...")})};resize_file_names();